CREATE VIEW [ADAPT].[CODE_GROUP_INDEX]
AS SELECT [GROUP_ID], [CONFIG_DISPLAY_NAME], [FORMAT], [LOCKED_DOWN], [LOOKUP_BY], [SORT_ORDER], [NODES_EXPANDED], [ALLOW_PARENT_SELECTION], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_CODE_GROUP_INDEX UNION ALL SELECT [GROUP_ID], [CONFIG_DISPLAY_NAME], [FORMAT], [LOCKED_DOWN], [LOOKUP_BY], [SORT_ORDER], [NODES_EXPANDED], [ALLOW_PARENT_SELECTION], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_CODE_GROUP_INDEX_History;
