CREATE VIEW [ADAPT].[DOCUMENTS]
AS SELECT [DOC_ID], [OWNER_ID], [DOC_CATEGORY], [DOC_NAME], [DOC_DESCRIPTION], [FILE_EXTENSION], [CREATED_DATE], [CREATED_BY], [UPDATED_BY], [UPDATED_DATE], [NOTES], [DEFAULT], [SIZE], [STATUS], [OWNER_TYPE], [PREVIEW_TYPE], [MODIFY_DATE], [PREVIEW_UPD_DATE], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_DOCUMENTS UNION ALL SELECT [DOC_ID], [OWNER_ID], [DOC_CATEGORY], [DOC_NAME], [DOC_DESCRIPTION], [FILE_EXTENSION], [CREATED_DATE], [CREATED_BY], [UPDATED_BY], [UPDATED_DATE], [NOTES], [DEFAULT], [SIZE], [STATUS], [OWNER_TYPE], [PREVIEW_TYPE], [MODIFY_DATE], [PREVIEW_UPD_DATE], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_DOCUMENTS_History;
GO
