CREATE VIEW [ADAPT].[DOCUMENT_TEMPLATES]
AS SELECT [DOC_TEMPLATE_ID], [CONFIG_NAME], [CREATED_DATE], [CREATED_BY], [UPDATED_DATE], [UPDATED_BY], [DOC_TYPE_ID], [LAST_USED_DATE], [OWNER_TYPE], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_DOCUMENT_TEMPLATES UNION ALL SELECT [DOC_TEMPLATE_ID], [CONFIG_NAME], [CREATED_DATE], [CREATED_BY], [UPDATED_DATE], [UPDATED_BY], [DOC_TYPE_ID], [LAST_USED_DATE], [OWNER_TYPE], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_DOCUMENT_TEMPLATES_History;
