CREATE VIEW [ADAPT].[MD_MULTI_NAMES]
AS SELECT [ID], [TYPE], [LANGUAGE], [NAME], [DESCRIPTION], [ICON_FILE_NAME], [SVG_FILE_NAME], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_MD_MULTI_NAMES UNION ALL SELECT [ID], [TYPE], [LANGUAGE], [NAME], [DESCRIPTION], [ICON_FILE_NAME], [SVG_FILE_NAME], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_MD_MULTI_NAMES_History;
