CREATE VIEW [ADAPT].[MD_ROLES]
AS SELECT [ROLE_ID], [PARENT_ID], [CONFIG_ROLE_NAME], [READ_ACCESS], [MODIFY_ACCESS], [DELETE_ACCESS], [LOCKED_DOWN], [ROLE_TYPE], [DEFAULT_ATTRIB1], [DEFAULT_ATTRIB2], [DEFAULT_ATTRIB3], [ALLOWS_DUMMY], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_MD_ROLES UNION ALL SELECT [ROLE_ID], [PARENT_ID], [CONFIG_ROLE_NAME], [READ_ACCESS], [MODIFY_ACCESS], [DELETE_ACCESS], [LOCKED_DOWN], [ROLE_TYPE], [DEFAULT_ATTRIB1], [DEFAULT_ATTRIB2], [DEFAULT_ATTRIB3], [ALLOWS_DUMMY], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_MD_ROLES_History;
