CREATE VIEW [ADAPT].[PROP_ACTION_GEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [ACT_TYPE], [SIGNED], [STATUS], [ADD_DATE], [COMPL_DATE], [OPEN_A_COUNT], [CLOSED_A_CNT], [FRT_ACTIV_SD], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_ACTION_GEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [ACT_TYPE], [SIGNED], [STATUS], [ADD_DATE], [COMPL_DATE], [OPEN_A_COUNT], [CLOSED_A_CNT], [FRT_ACTIV_SD], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_ACTION_GEN_History;
