CREATE VIEW [ADAPT].[PROP_ACTIVITY_GEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [NAME], [START_DATE], [DUE_DATE], [STATUS], [ACT_COM_DATE], [NUMB_OF_DAYS], [WHO_WILL_HLP], [RLT_SP_NEED], [BARRIER], [ACT_NUMBER], [INTERVEN], [ASSIGNMENT], [TASTART_DT], [TA_FAILST_DT], [MWRA], [ACT_TYPE], [SIG_EVENT], [EVIDENCE_NT], [ACTIVITY_ID], [ACT_DESCRIPT], [NUMB_OF_HRS], [HOURS_SCHED], [OUTCOME], [CONT_METHOD], [BARR_SPEC], [MAND_FIELD], [LTU], [NEO], [PONUM], [SERVICE], [COMPLETE_FL], [NEO_CODE], [PCAT_SCORE], [P_DATE_TAKEN], [P_SCORE], [VENUE], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_ACTIVITY_GEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [NAME], [START_DATE], [DUE_DATE], [STATUS], [ACT_COM_DATE], [NUMB_OF_DAYS], [WHO_WILL_HLP], [RLT_SP_NEED], [BARRIER], [ACT_NUMBER], [INTERVEN], [ASSIGNMENT], [TASTART_DT], [TA_FAILST_DT], [MWRA], [ACT_TYPE], [SIG_EVENT], [EVIDENCE_NT], [ACTIVITY_ID], [ACT_DESCRIPT], [NUMB_OF_HRS], [HOURS_SCHED], [OUTCOME], [CONT_METHOD], [BARR_SPEC], [MAND_FIELD], [LTU], [NEO], [PONUM], [SERVICE], [COMPLETE_FL], [NEO_CODE], [PCAT_SCORE], [P_DATE_TAKEN], [P_SCORE], [VENUE], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_ACTIVITY_GEN_History;
