CREATE VIEW [ADAPT].[PROP_BARRIER_GEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [START_DT], [END_DT], [PRIORITY], [STATUS], [NAME], [BARRIER], [BARRIER_TYPE], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_BARRIER_GEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [START_DT], [END_DT], [PRIORITY], [STATUS], [NAME], [BARRIER], [BARRIER_TYPE], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_BARRIER_GEN_History; 
