CREATE VIEW [ADAPT].[PROP_CAND_PREF]
AS SELECT [BISUNIQUEID], [REFERENCE], [SMOKING_ENV], [PART_TIME], [OPT_OUT_WTD], [RATE_REQ], [SALARY_REQ], [OTE_REQ], [LOCATION], [P_TEMP], [P_PERM], [P_CONTR], [RATETYPE_REQ], [RELOCATE], [LOOK_FOR], [SALARY_DES], [RATE_DES], [CONTACT], [DISTANCE], [HOURS_PREF], [CONTACTABLE], [HOURS_TO], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_CAND_PREF UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [SMOKING_ENV], [PART_TIME], [OPT_OUT_WTD], [RATE_REQ], [SALARY_REQ], [OTE_REQ], [LOCATION], [P_TEMP], [P_PERM], [P_CONTR], [RATETYPE_REQ], [RELOCATE], [LOOK_FOR], [SALARY_DES], [RATE_DES], [CONTACT], [DISTANCE], [HOURS_PREF], [CONTACTABLE], [HOURS_TO], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_CAND_PREF_History; 
