CREATE VIEW [ADAPT].[PROP_DISABILITY]
AS SELECT [BISUNIQUEID], [REFERENCE], [DISABILITY], [DISLEVEL], [CONTRACT], [PRIMARY_YN], [PRIMARY_DIS], [AREA], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_DISABILITY UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [DISABILITY], [DISLEVEL], [CONTRACT], [PRIMARY_YN], [PRIMARY_DIS], [AREA], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_DISABILITY_History;
