CREATE VIEW [ADAPT].[PROP_EMAIL]
AS SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], REPLICATE('X',LEN([EMAIL_ADD])) AS [EMAIL_ADD], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_EMAIL UNION ALL SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], REPLICATE('X',LEN([EMAIL_ADD])) AS [EMAIL_ADD], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_EMAIL_History; 
