CREATE VIEW [ADAPT].[PROP_JOB_BEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [DRESS_CODE], [SMOK_ENV], [W_HOURS], [HOL_ENT], [PEN_INF], REPLICATE('X',LEN([HEALTH_INS])) AS [HEALTH_INS], [OVER_INF], [LIFE_ASSUR], [BONUS_INF], [ON_SITE_PARK], [OTHER], [SEAS_PEAKS], [AW_HOURS], [TRANSPORT], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_JOB_BEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [DRESS_CODE], [SMOK_ENV], [W_HOURS], [HOL_ENT], [PEN_INF], REPLICATE('X',LEN([HEALTH_INS])) AS [HEALTH_INS], [OVER_INF], [LIFE_ASSUR], [BONUS_INF], [ON_SITE_PARK], [OTHER], [SEAS_PEAKS], [AW_HOURS], [TRANSPORT], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_JOB_BEN_History;
