CREATE VIEW [ADAPT].[PROP_JOB_HIST]
AS SELECT [BISUNIQUEID], [REFERENCE], [FILLED_DT], [FILLED_TM], [NO_CV_SENT], [NO_IV_ARR], [NO_IV_ATT], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_JOB_HIST UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [FILLED_DT], [FILLED_TM], [NO_CV_SENT], [NO_IV_ARR], [NO_IV_ATT], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_JOB_HIST_History;
