CREATE VIEW [ADAPT].[PROP_JOB_JOBBOARD]
AS SELECT [BISUNIQUEID], [PRIMREF], [JOB_TITLE], [SALARY_FROM], [SALARY_TO], [RATE_FROM], [RATE_TO], REPLICATE('X',LEN([EMAIL_RESP])) AS [EMAIL_RESP], [LOCATION], [EXP_TO_HOME], [EXP_TO_JB], [VALID_FROM], [VALID_T0], [AUTH], [CONTACT_USER], REPLICATE('X',LEN([EMAIL])) AS [EMAIL], [START_DATE], [END_DATE], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_JOB_JOBBOARD;
