CREATE VIEW [ADAPT].[PROP_OWN_CONS] AS 
SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], [CONSULTANT], null as [TEAM], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_OWN_CONS UNION ALL SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], [CONSULTANT], null as [TEAM], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_OWN_CONS_History ;