CREATE VIEW [ADAPT].[PROP_PJOB_GEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [HOURS_TYPE], [FIX_TERM], [SAL_FROM], [SAL_TO], [OTE], [OPEN_SINCE], [NOT_PERIOD], [EDUC_LEVEL], [TEMP_REQ], [PAY_INT], [SAL_REV], [ASALARY], [NO_REQ], [SAL_VAL1], [SAL_VAL2], [NO_MONTHS], [NO_SOFAR], [RENUMUNIT], [EXT_FILLED], [NO_REMAIN], [TEMPPERM], [WAGE_CAT], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_PJOB_GEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [HOURS_TYPE], [FIX_TERM], [SAL_FROM], [SAL_TO], [OTE], [OPEN_SINCE], [NOT_PERIOD], [EDUC_LEVEL], [TEMP_REQ], [PAY_INT], [SAL_REV], [ASALARY], [NO_REQ], [SAL_VAL1], [SAL_VAL2], [NO_MONTHS], [NO_SOFAR], [RENUMUNIT], [EXT_FILLED], [NO_REMAIN], [TEMPPERM], [WAGE_CAT], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_PJOB_GEN_History;
