CREATE VIEW [ADAPT].[PROP_RTE_HRS_HIST]
AS SELECT [BISUNIQUEID], [EFFECT_TO], [REFERENCE], [FRI], [MON], [SAT], [SUN], [THU], [TUE], [WED], [WEEKLY_HRS], [EFFECT_FROM], [HOURLY_RATE], [STATUS], [DATE_CHANGED], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_RTE_HRS_HIST UNION ALL SELECT [BISUNIQUEID], [EFFECT_TO], [REFERENCE], [FRI], [MON], [SAT], [SUN], [THU], [TUE], [WED], [WEEKLY_HRS], [EFFECT_FROM], [HOURLY_RATE], [STATUS], [DATE_CHANGED], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_RTE_HRS_HIST_History;
