CREATE VIEW [ADAPT].[PROP_SHORT_GEN]
AS SELECT [BISUNIQUEID], [REFERENCE], [DATE_SHORT], [TIME_SHORT], [STATUS], [LAST_CV_DT], [LAST_CV_TM], [LAST_IV_DT], [LAST_IV_TM], [SHORT_BY], [SHORT_ID], [LAST_CV_BY], [LAST_IV_BY], [LAST_OF_DT], [LAST_OF_TM], [LAST_OF_BY], [OFFER_SAL], [REJ_OF_DT], [REJ_OF_TM], [REJ_OF_BY], [REJ_DT], [REJ_TM], [REJ_BY], [ASSIG_DATE], [LAST_IV_ST], [REJECTED_BY], [LONGLISTED], [SH_VIA_STAGE], [REJ_RSN], [REJ_OFF_RSN], [REJ_REASON], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_SHORT_GEN UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [DATE_SHORT], [TIME_SHORT], [STATUS], [LAST_CV_DT], [LAST_CV_TM], [LAST_IV_DT], [LAST_IV_TM], [SHORT_BY], [SHORT_ID], [LAST_CV_BY], [LAST_IV_BY], [LAST_OF_DT], [LAST_OF_TM], [LAST_OF_BY], [OFFER_SAL], [REJ_OF_DT], [REJ_OF_TM], [REJ_OF_BY], [REJ_DT], [REJ_TM], [REJ_BY], [ASSIG_DATE], [LAST_IV_ST], [REJECTED_BY], [LONGLISTED], [SH_VIA_STAGE], [REJ_RSN], [REJ_OFF_RSN], [REJ_REASON], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_SHORT_GEN_History;
