CREATE VIEW [ADAPT].[PROP_TELEPHONE]
AS SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], [TEL_NUMBER], [EXTENSION], [CAN_CONTACT], [CAN_SMS], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_TELEPHONE UNION ALL SELECT [BISUNIQUEID], [OCC_ID], [REFERENCE], [TEL_NUMBER], [EXTENSION], [CAN_CONTACT], [CAN_SMS], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_TELEPHONE_History; 
