CREATE VIEW [ADAPT].[PROP_WP_COM]
AS SELECT [BISUNIQUEID], [REFERENCE], [NOTES], [CONT_BY], [OUTCOME], [METHOD], [COM_DATE], [DIS_FOLLOWUP], [COMTYPE], [ACTIVITY], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_WP_COM UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [NOTES], [CONT_BY], [OUTCOME], [METHOD], [COM_DATE], [DIS_FOLLOWUP], [COMTYPE], [ACTIVITY], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_WP_COM_History;
