CREATE VIEW [ADAPT].[PROP_X_SHORT_CAND]
AS SELECT [BISUNIQUEID], [SHORTLIST], [CANDIDATE], [JOB], [CLIENT], [CONTACT], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_X_SHORT_CAND UNION ALL SELECT [BISUNIQUEID], [SHORTLIST], [CANDIDATE], [JOB], [CLIENT], [CONTACT], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_X_SHORT_CAND_History;
