CREATE VIEW [ADAPT].[PROP_X_WP_EMPL]
AS SELECT [BISUNIQUEID], [REFERENCE], [EMPLOYEE], [PRIMARY_YN], [ValidFrom], [ValidTo], [row_sha2], CAST(1 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_X_WP_EMPL UNION ALL SELECT [BISUNIQUEID], [REFERENCE], [EMPLOYEE], [PRIMARY_YN], [ValidFrom], [ValidTo], [row_sha2], CAST(0 AS BIT) AS IsCurrent FROM DELTA.ADAPT_PROP_X_WP_EMPL_History;